import "./styles.scss";

import { initialCardState } from "./Configs";

const reducer = (state, action) => {
  switch (action.type) {
    case "SAVE":
      return state.map((card) => {
        if (card.id === action.id) {
          return { ...card };
        }
      });
    default:
      return state;
  }
};

const AddCard = () => {
  const [cardState, dispatch] = useReducer(reducer, initialCardState);

  const saveCard = (card) => {
    dispatch({ type: "SAVE", id: card.id });
  };

  return (
    <div className="add-card-ui">
      <h1>Add a New Card</h1>
      <form>
        <label>
          Title
          <input
            type="text"
            placeholder="enter card title"
            value={title}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <label>
          Description
          <textarea value={description} onChange={handleChange} />
        </label>
        <label>
          Link
          <input
            type="text"
            placeholder="paste link url"
            value={link}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <input
          type="submit"
          name="submit"
          role="submit"
          value="Save"
          onClick={saveCard}
        />
      </form>
    </div>
  );
};

export default AddCard;
