import "./styles.scss";
import React, { useState } from "react";
import Card from "./Card";
import AddCard from "./AddCard";
import Modal from "@mui/material/Modal";
import { KanbanBoardContext } from "./context";

const Cards = () => {
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const renderCardRows = (cardRows) => {
    return cardRows.map((card, key) => {
      return <Card key={`card-${key}`} {...card}></Card>;
    });
  };

  const renderAddCardButton = (colorCode) => {
    return (
      <div
        className="plus-sign"
        style={{
          border: `1px dotted ${colorCode}`,
          padding: "3rem",
          margin: "0.5rem",
        }}
        onClick={handleOpen}
      >
        <i className="fa-thin fa-plus"></i>
      </div>
    );
  };

  const renderCards = (cardsMock) => {
    return cardsMock.map(({ cardsState, cardRows, colorCode }, key) => {
      return (
        <React.Fragment key={`card-mock-${key}`}>
          <div className="cards">
            <div
              style={{
                backgroundColor: colorCode,
                color: "white",
                padding: ".4rem",
              }}
            >
              {cardsState}
            </div>
            {cardRows && cardRows.length > 0 && renderCardRows(cardRows)}
            {renderAddCardButton(colorCode)}
          </div>
          {open && (
            <Modal
              open={open}
              onClose={handleClose}
              aria-labelledby="modal-modal-title"
              aria-describedby="modal-modal-description"
            >
              <AddCard></AddCard>
            </Modal>
          )}
        </React.Fragment>
      );
    });
  };

  return (
    <KanbanBoardContext.Consumer>
      {(cardsMock) => renderFeaturedContent(currencySymbolContextValue)}
    </KanbanBoardContext.Consumer>
  );
};

export default Cards;
