import "./styles.scss";
import React, { useState } from "react";
import Card from "./Card";

const Cards = ({ cardsMock }) => {
  const renderCardRows = (cardRows) => {
    return cardRows.map((card) => {
      return <Card {...card}></Card>;
    });
  };

  const renderAddCardButton = (colorCode) => {
    return (
      <div
        className="plus-sign"
        style={{
          border: `1px dotted ${colorCode}`,
          padding: "3rem",
          margin: "0.5rem",
        }}
      >
        <i class="fa-thin fa-plus"></i>
      </div>
    );
  };

  return cardsMock.map(({ cardsState, cardRows, colorCode }) => {
    console.log("cardsState, cardRows :", cardsState, cardRows);
    return (
      <div className="cards">
        <div
          style={{
            backgroundColor: colorCode,
            color: "white",
            padding: ".4rem",
          }}
        >
          {cardsState}
        </div>
        {cardRows && cardRows.length > 0 && renderCardRows(cardRows)}
        {renderAddCardButton(colorCode)}
      </div>
    );
  });
};

export default Cards;
