import "./styles.scss";
import Modal from "react-modal";
import AddCard from "./AddCard";
import React, { useState } from "react";
import { customStyles } from "./Configs";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import { cardsMock } from "./Configs";
import Cards from "./Cards";

// const tagsList = [{ label: "iOS" }, { label: "Android" }, { label: "Windows" }];

const tagsSet = cardsMock.reduce((acc, { cardRows }) => {
  const { cardTags } = cardRows;
  cardTags.map(({ title }) => acc.add(title));
}, new Set());

export default function Dashboard() {
  const [modalIsOpen, setIsOpen] = useState(false);
  const [searchVal, setSearchVal] = useState("");
  const [visibleCards, setVisibleCards] = useState(cardsMock);

  const setSearchState = (event) => {
    setSearchVal(event.target.value);
  };

  const openModal = () => {
    setIsOpen(true);
  };

  const afterOpenModal = () => {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "#f00";
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  const renderTagsFilter = () => {
    return (
      <Autocomplete
        disablePortal
        id="select-tag"
        options={tagsList}
        sx={{ width: 100 }}
        renderInput={(params) => <TextField {...params} label="Tags" />}
      />
    );
  };

  const filterSearchResults = () => {
    // setVisibleCards
    const filteredCardsMock = cardsMock.map(({ cardRows }) =>
      cardRows.map((cardRow) =>
        cardRow.filter(
          ({ cardTitle, cardDesc, cardVersion }) =>
            cardTitle.includes(searchVal) ||
            cardDesc.includes(searchVal) ||
            cardVersion.includes(searchVal)
        )
      )
    );

    setVisibleCards(filteredCardsMock);
  };

  return (
    <div className="dashboard">
      <h1>Kanban Board</h1>
      <div className="search-tags-container">
        <div className="search-wrapper">
          <input
            placeholder="search this board"
            name="search"
            value={searchVal}
            onKeyDown={filterSearchResults}
            onChange={setSearchState}
            className="search-input"
          ></input>
          <i className="fa fa-search search-icon" aria-hidden="true"></i>
        </div>
        {renderTagsFilter()}
      </div>
      <div className="cards-container">
        <Cards cardsMock={visibleCards}></Cards>
      </div>
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <AddCard></AddCard>
      </Modal>
    </div>
  );
}
